package com.galang.weatherapp.data.networkimport com.galang.weather.BuildConfigimport com.galang.weatherapp.data.response.ForecastResponseimport com.galang.weatherapp.data.response.WeatherResponseimport retrofit2.Callimport retrofit2.http.GETimport retrofit2.http.Queryinterface ApiService {    @GET("weather")    fun weatherByCity(        @Query("q") city : String,        @Query("appid") apiKey: String? = BuildConfig.API_KEY    ): Call<WeatherResponse>    @GET("forecast")    fun forecastByCity(        @Query("q") city : String,        @Query("appid") apiKey: String? = BuildConfig.API_KEY    ): Call<ForecastResponse>    @GET("weather")    fun weatherByCurrentLocation(        @Query("lat") lat : Double,        @Query("appid") lon: Double,        @Query("appid") apiKey: String? = BuildConfig.API_KEY    ): Call<WeatherResponse>    @GET("forecast")    fun forecastByCurrentLocation(        @Query("lat") lat : Double,        @Query("appid") lon: Double,        @Query("appid") apiKey: String? = BuildConfig.API_KEY    ): Call<ForecastResponse>//    @GET("weather")//    fun getWeatherByCity(//        @Query("q") city: String,//        @Query("appid") appId: String? = BuildConfig.API_KEY,//    ): Call<ForecastResponse>}